$(function() {
var text = $('.c-content').text(); // .post-excerpt yerine yazının bulunduğu div id - class ismi çoğunluk olarak .post-body olabilir
var charsLength = text.length;
var wordsCount = text.split(' ').length;
$('#words').html(' ' + wordsCount + ' word'); // kelime sayısını göstermek için
$('#chars').html(' ' + charsLength + ' karakter'); // karakter sayısını göstermek için
});



